import React from 'react';

const About = () => {
  return (
    <section id="about" className="py-24 bg-white relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl font-extrabold text-gray-900 mb-3">About Me</h2>
          <p className="text-lg text-gray-500">Get to know who I am and what I bring to the table.</p>
          <div className="w-24 h-1 bg-indigo-600 mx-auto mt-4 rounded-full"></div>
        </div>

        {/* Content Grid */}
        <div className="grid md:grid-cols-2 gap-12 items-start">
          
          {/* Left - Personal Intro */}
          <div className="space-y-6">
            <h3 className="text-3xl font-bold text-gray-900">Hey, I'm Siddharth ðŸ‘‹</h3>
            <p className="text-gray-600 text-lg leading-relaxed">
              I'm a Full Stack Developer with a passion for crafting seamless digital experiences.
              From intuitive UI designs to robust backend systems, I enjoy building applications that are fast, scalable, and user-centric.
            </p>
            <p className="text-gray-600 text-lg leading-relaxed">
              Currently, I focus on **React.js**, **Node.js**, and **Cloud Deployments**, but I'm always exploring new technologies and pushing my boundaries.
              I believe in writing clean, maintainable code and continuously improving as a developer.
            </p>
            <p className="text-gray-600 text-lg leading-relaxed">
              Apart from coding, I'm into tech blogging, open-source contributions, and occasionally mentoring fellow developers.
            </p>
          </div>

          {/* Right - Skill Highlights */}
          <div className="grid gap-6">
            {[
              {
                title: "Frontend Development",
                desc: "Responsive, dynamic UI with React, TailwindCSS, and animations.",
              },
              {
                title: "Backend Development",
                desc: "Building APIs & services using Node.js, Express, and databases.",
              },
              {
                title: "Cloud & DevOps",
                desc: "Deploying and scaling apps using Vercel, Netlify, and CI/CD workflows.",
              },
              {
                title: "Problem Solving",
                desc: "Strong debugging and algorithmic thinking for efficient solutions.",
              },
            ].map((item, idx) => (
              <div key={idx} className="flex items-start space-x-4 group">
                <div className="flex-shrink-0 w-10 h-10 bg-indigo-100 text-indigo-600 rounded-full flex items-center justify-center group-hover:bg-indigo-600 group-hover:text-white transition duration-300">
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-900 group-hover:text-indigo-600 transition">{item.title}</h4>
                  <p className="text-gray-600">{item.desc}</p>
                </div>
              </div>
            ))}
          </div>

        </div>

        {/* CTA Button */}
        <div className="mt-16 text-center">
          <a
            href="#contact"
            className="inline-flex items-center px-8 py-3 border border-transparent text-base font-medium rounded-full text-white bg-indigo-600 hover:bg-indigo-700 transition duration-300"
          >
            Let's Collaborate
            <svg className="ml-3 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 8l4 4m0 0l-4 4m4-4H3" />
            </svg>
          </a>
        </div>

      </div>
    </section>
  );
};

export default About;
