import React from 'react';

const Projects = () => {
  const projects = [
    // AI / Machine Learning Projects
    {
      id: 1,
      title: "Personalized News Recommender",
      description: "Recommends news articles based on user preferences using NLP and ML algorithms.",
      techStack: ["Python", "Flask", "TF-IDF", "BERT", "Cosine Similarity"],
      category: "AI/ML"
    },
    {
      id: 2,
      title: "Customer Churn Prediction",
      description: "Predicts customer churn using classification models and imbalanced dataset handling.",
      techStack: ["Python", "Pandas", "Random Forest", "Gradient Boosting"],
      category: "AI/ML"
    },
    {
      id: 3,
      title: "Reinforcement Learning Agent",
      description: "Trained an AI agent to play a game using Q-Learning and Deep Q-Networks.",
      techStack: ["Python", "TensorFlow", "OpenAI Gym"],
      category: "AI/ML"
    },
    // Data Science Projects
    {
      id: 4,
      title: "Public Dataset EDA & Visualization",
      description: "Exploratory Data Analysis and visualization of complex public datasets.",
      techStack: ["Python", "Pandas", "Matplotlib", "Seaborn"],
      category: "Data Science"
    },
    {
      id: 5,
      title: "A/B Testing Analysis",
      description: "Performed statistical analysis on A/B test data to determine product impact.",
      techStack: ["Python", "SciPy", "Pandas", "Statistics"],
      category: "Data Science"
    },
    {
      id: 6,
      title: "Real-Time Data Dashboard",
      description: "Interactive dashboard visualizing real-time data from APIs.",
      techStack: ["Streamlit", "Plotly", "Python", "APIs"],
      category: "Data Science"
    },
    // Computer Vision Projects
    {
      id: 7,
      title: "Digital Sticker Generator",
      description: "Removes background and generates personalized stickers from user-uploaded images.",
      techStack: ["Python", "OpenCV", "Mask R-CNN", "Flask"],
      category: "Computer Vision"
    },
    {
      id: 8,
      title: "License Plate Detection & OCR",
      description: "Detects license plates in images and extracts text using OCR techniques.",
      techStack: ["YOLO", "OpenCV", "Tesseract", "Python"],
      category: "Computer Vision"
    },
    {
      id: 9,
      title: "Real-Time Human Pose Estimation",
      description: "Tracks human body joints in real-time using webcam feed.",
      techStack: ["MediaPipe", "OpenPose", "Python", "OpenCV"],
      category: "Computer Vision"
    }
  ];

  return (
    <section id="projects" className="py-20 bg-gradient-to-b from-[#3730A3] to-[#312E81]">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-white mb-4">My Projects</h2>
          <div className="w-24 h-1 bg-yellow-300 mx-auto mb-8"></div>
        </div>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project) => (
            <div key={project.id} className="bg-white/10 rounded-lg shadow-md p-6 hover:shadow-lg transition border border-white/10">
              <h3 className="text-xl font-semibold text-yellow-300 mb-2">{project.title}</h3>
              <p className="text-indigo-100 mb-4 text-sm">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-2">
                {project.techStack.map((tech, index) => (
                  <span key={index} className="bg-indigo-900/60 text-yellow-200 text-xs px-2 py-1 rounded-full">{tech}</span>
                ))}
              </div>
              <span className="text-yellow-300 text-xs font-medium">{project.category}</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
